const reducer = (state, action) => {
    switch (action.type) {
        case 'inc':
            return {state,count1: state.count1 +1 }
        case 'dec':
            return {state,count1: state.count1 -1 }
        case 'reset':
            return {state,count1: 0 }
        case 'install':
            return {state,count1: action.payload }
    }
}
export default reducer;
