import {useRef} from "react";
import {ADD, useCatReducer} from "./reducers";


function App() {

    const catRef = useRef();
    const [state, dispatch] = useCatReducer();

    const addCat = () => {
        const newCat = catRef.current.value;
        dispatch({type:ADD, payload:{cat:newCat}})
    }

    return (
        <div>
            <div>
                <input type="text" ref={catRef} placeholder={'enter cat name'}/>
                <button onClick={addCat}>Add</button>
            </div>
            <hr/>
            <div>
                {state.map(cat => <div key={cat.id}>{cat.name}
<button onClick={()=> dispatch({type:DELETE, payload:{id:cat.id}})}>Delete</button>
                </div>)}
            </div>

        </div>
    );
}

export default App;
